// useContext()
// 1.create context --React.CreateContext() --created
// 2.create provider
// 3.consumer
// useRef()


import { useState } from "react"


let Context=React.createContext()

export default function App1(){
    const [data,setData]=useState("React JS")
    return(
        <div>
            <Context.Provider value={data}>
            <h1>Main component</h1>
            <Parent />
            </Context.Provider>
        </div>
    )
}
// here we are doing destructing method so we are entering a value as a parameter of parent function 
function Parent(){  
    return(
        <div>
            <h2>Parent component</h2>
            <Child />
        </div>
    )
}

function Child(){
    const value=useContext
    return(
        <div>
            <h3>Child component</h3>
        </div>
    )
}
