function App(){
    const [data,setData]=useState("React JS")
    const [count,setCount]=useState(0)
    const change=()=>{
        setData("Hello")
    }
    const inc=()=>{
        setCount(count+1)
    }
    return(
        <div>
            <h1>Hooks Methods {data}</h1>
            <Data value={data} count={count}/>  
            <button onClick={change}>Update</button>  
            <br/><br/>
            <p>Count:{count}</p>
            <button onClick={inc}>Inc</button>
        </div>
    )
}


import { useState } from "react";
import {useNavigate} from "react-router-dom";
function Register() {
    const [user,setUser]=useState("")
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
    const [cpswd,setCpswd]=useState("")
    const navigate=useNavigate("")
    const changeUser=(e)=>{
        setUser(e.target.value)
    }
    const changeEmail=(e)=>{
        setEmail(e.target.value)
    }
    const changePswd=(e)=>{
        setPswd(e.target.value)
    }
    const changeCpswd=(e)=>{
        setCpswd(e.target.value)
    }
    const submitData=()=>{
        if(user.length<=5){
            alert("user should be enter > 5 character")
        }
        else if(pswd != cpswd){
            alert("password does not match")
        }
        else{
            alert("Form Sucessfully submited")
            navigate("/login")
        }
    }
    return (
        <div>
            <form onClick={submitData}>  
                <label>Username : </label>
                <input value={user} required type="text" onChange={changeUser} />
                <br /><br />
                <label>Email : </label>
                <input value={email} required type="email" onChange={changeEmail} />
                <br /><br />
                <label>Password : </label>
                <input value={pswd} required type="password" onChange={changePswd}/>
                <br /><br />
                <label>Confirm password :</label>
                <input value={cpswd} required type="password" onChange={(changeCpswd)} />
                <br /><br />
                <input type="submit" value="Register" />
            </form>
        </div>
    )
}
export default Register;
