import { useState } from "react";
import { useSearchParams } from "react-router-dom";


function EmpForm() {
    const [id,setId]=useState("")
    const [name,setName]=useState("")
    const [city,setCity]=useState("")
    const [mobile,setMobile]=useState("")
    return (
        <div className="container">
            <h1>ADD NEW EMPLOYE DATA</h1>
            <form>
                <div class="mb-3">
                    <label  class="form-label">ID</label>
                    <input value={id} type="text" onChange={e=>setId(e.target.value)} disabled="disabled" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                
                </div>
                <div class="mb-3">
                    <label  class="form-label">Name</label>
                    <input value={name} type="text" class="form-control" id="exampleInputPassword1" />
                </div>
                <div class="mb-3">
                    <label  class="form-label">City</label>
                    <input value={city} type="text" class="form-control" id="exampleInputPassword1" />
                </div>
                <div class="mb-3">
                    <label  class="form-label">Mobile</label>
                    <input value={mobile} type="number" class="form-control" id="exampleInputPassword1" />
                </div>
                
                
                <button type="submit" class="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
export default EmpForm;
