import { useState } from "react";
import { Link } from "react-router-dom";
 
function EmpForm() {
    const [id,setId]=useState("")
    const [name,setName]=useState("")
    const [city,setCity]=useState("")
    const [mobile,setMobile]=useState("")


    const sendData=()=>{
        const data={id,name,city,mobile}
        fetch("http://localhost:3007/Employe ",{
            method:"POST",
            headers:{"Content-type":"application/json"},
            body:JSON.stringify(data)
        })
        .then(()=>{
            alert("Data saved!..")
        })
    }
    return (
        <div className="container">
            <h1>ADD NEW EMPLOYE DATA</h1>
            <form onSubmit={sendData}> 
                <div class="mb-3">
                    <label  class="form-label">ID</label>
                    <input value={id} type="text" onChange={e=>setId(e.target.value )} disabled="disabled" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                
                </div>
                <div class="mb-3">
                    <label  class="form-label">Name</label>
                    <input value={name} type="text" onChange={e=>setName(e.target.value)} class="form-control" id="exampleInputPassword1" />
                </div>
                <div class="mb-3">
                    <label  class="form-label">City</label>
                    <input value={city} type="text" onChange={e=>setCity(e.target.value)} class="form-control" id="exampleInputPassword1" />
                </div>
                <div class="mb-3">
                    <label  class="form-label">Mobile</label>
                    <input value={mobile} type="number" onChange={e=>setMobile(e.target.value)} class="form-control" id="exampleInputPassword1" />
                </div>
                
                
                <button type="submit" class="btn btn-primary">Submit</button><br/><br/>
                <Link to="/" class="btn btn-secondary">Back</Link>
            </form>
        </div>
    )
}
export default EmpForm;
