import React from "react";

class Login extends React.Component{
    constructor(){
        super()
        this.state={
            user:"",
            email:"",
            pswd:""
        }
    }
    updateUser=(e)=>{
        console.log(e.target.value)

        this.setState({
            user:e.target.value
        })  
    }
    updateEmail=(e)=>{
        this.setState({
            email:e.target.value
        })
    }
    updatePassword=(e)=>{
        this.setState({
            pswd:e.target.value
        })
    }
    submitForm=()=>{
        localStorage.getItem("user")
        localStorage.getItem("email")
        localStorage.getItem("password")
        if(this.state.user != x || this.state.email !=y || this.state.pswd !=z){
            alert("check the credintials")
        }
        else{
            alert("Login is successfull..")
        }

    }
    render(){
        return(
            <div>
                
                <form onSubmit={this.submitForm}>
                <label>Username : </label>
                <input value={this.state.user} required type="text" onChange={this.updateUser}/>
                <br/><br/>
                <label>Email : </label>
                <input value={this.state.email} required type="email" onChange={this.updateEmail} />
                <br/><br/>
                <label>Password : </label>
                <input value={this.state.pswd} required type="password" onChange={this.updatePassword}/>
                <br/><br/>
                    <input type="submit" value="Login"/>
                </form>
            </div>
        )
    }
}
export default Login;
