import React, { useState } from "react";

const Login = () => {
  const [user, setUser] = useState("");
  const [email, setEmail] = useState("");
  const [pswd, setPswd] = useState("");

  const updateUser = (e) => {
    console.log(e.target.value);
    setUser(e.target.value);
  };

  const updateEmail = (e) => {
    setEmail(e.target.value);
  };

  const updatePassword = (e) => {
    setPswd(e.target.value);
  };

  const submitForm = (e) => {
    e.preventDefault();
    let x = localStorage.getItem("user");
    let y = localStorage.getItem("email");
    let z = localStorage.getItem("password");
    if (user !== x || email !== y || pswd !== z) {
      alert("Check the credentials.");
    } else {
      alert("Logged in!");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={submitForm}>
        <label>Username:</label>
        <input value={user} type="text" required onChange={updateUser} />
        <br />
        <br />
        <label>Email:</label>
        <input value={email} type="email" required onChange={updateEmail} />
        <br />
        <br />
        <label>Password:</label>
        <input
          value={pswd}
          required
          onChange={updatePassword}
          type="password"
        />
        <br />
        <br />
        <input type="submit" value="Login" />
      </form>
    </div>
  );
};

export default Login;








