// React js completely based in components
// There are two types
// 1.Class based components : Stateful components -> which have own state
    // oops based
// 2.Functional components : stateless components
    // Hooks methods

// How to make components -> using JSX rules
// 1.Function name / class name - first letter should be capital
// 2.return key word
// 3.Any one block element - div , main , section
// 4.id = id , class = calssName
// 5.Html attributes are camel case
    // eg : onclick - onclick
//6.Every tag is open & close is mandatory ex : <br> </br> r <br/>
// export component
// export default component

import "./index.css"
const style={
    color:"yellow",
    backgroundColor:"green"
}

export function App(){
    return(
        <div className="a">
            <h1 style={{color:"red",backgroundColor:'green'}}>React JS</h1>
            <p>This is my paragraph</p>
            <p>This para is too long</p>
            <button>Click</button>
        </div>
    )
}

// export default App; 


export function App1(){
    return(
        <div>
            <h1 style={style}>App1 Components</h1>
        </div>
    )
}
