// useContext()
// useRef()

import { useState } from "react"

export default function App1(){
    const [data,setData]=useState("React JS")
    return(
        <div>
            <h1>Main component</h1>
            <Parent value={data}/>
        </div>
    )
}
// here we are doing destructing method so we are entering a value as a parameter of parent function 
function Parent({value}){  
    return(
        <div>
            <h1>Parent component {value}</h1>
            <Child data={value}/>
        </div>
    )
}

function Child({data}){
    return(
        <div>
            <h1>Child component {data} </h1>
        </div>
    )
}
