function App() {
    const [user,setUser]=useState("")
    const [email,setEmail]=useState("")
    const [pswd,setPswd]=useState("")
    const [cpswd,setCpswd]=useState("")
    const changeUser=(e)=>{
        setUser(e.target.value)
    }
    const changeEmail=(e)=>{
        setEmail(e.target,value)
    }
    const changePswd=(e)=>{
        setPswd(e.target.value)
    }
    const changeCpswd=(e)=>{
        setCpswd(e.target.value)
    }
    const submitData=()=>{
        if(user.length<=5){
            alert("user should be enter > 5 character")
        }
        else if(pswd != cpswd){
            alert
        }
    }
    return (
        <div>
            <form>
                <label>Username : </label>
                <input value={user} required type="text" onChange={changeUser} />
                <br /><br />
                <label>Email : </label>
                <input value={email} required type="email" onChange={changeEmail} />
                <br /><br />
                <label>Password : </label>
                <input value={pswd} required type="password" onChange={changePswd}/>
                <br /><br />
                <label>Confirm password :</label>
                <input value={cpswd} required type="password" onChange={(changeCpswd)} />
                <br /><br />
                <input type="submit" value="Register" />
            </form>
        </div>
    )
}
export default App;
