import { useEffect,useState } from "react";
import "./index.css"
import { Link, useNavigate } from "react-router-dom";

function App(){
    const [data,setData]=useState(null)
    const navigate=useNavigate()
    useEffect(()=>{
        fetch("http://localhost:3007/Student")
        .then((res)=>{
            return res.json()
        })
        .then((resp)=>{
            console.groupCollapsed(resp)
            setData(resp)
        })
    },[]) 

    const deleteData=(id)=>{
        if(window.confirm("Do you want to delete it..")){
        fetch("http://localhost:3007/Student/"+id,{
            method:"DELETE"
        })
        .then((s)=>{
            alert("Deleted successfully")
            window.location.reload()
        })
        .catch((err)=>{
            console.log(err)
        })
    }
    }

const EditData=(id)=>{
    navigate("/empedit/"+id)
}
const DetailsData=(id)=>{
    navigate("/studetails/"+id)
}

    return(
        <div className="container">
            <div className="card">
                <div className="card-title">
                    <h1>Student Data Management System</h1>
                </div>
                <div className="card-body">
                <Link to="/form" className="btn btn-success">Add New (+)</Link><br/><br/>
                <form>
                <input type="text" placeholder="Filter Records" className="form-control" id="exampleInputPassword1"/>
                </form>

            <table className="table table-borderd">
                <thead className="table-dark text-white">
                    <tr>
                        <th>Student Id</th>
                        <th>Class</th>
                        <th>Name</th>
                        <th>City</th>
                        <th>Mobile</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {data?.map((item)=>(

                   
                    <tr key={item.id}>
{/* if we click on check it as print in console  <input type="checkbox" /> */}
                        <td>
                          {item.id}</td> 
                        <td>{item.clas}</td>
                        <td>{item.name}</td>
                        <td>{item.city}</td>
                        <td>{item.mobile}</td>
                        <td>
                            <button onClick={()=>{deleteData(item.id)}} className="btn btn-danger">Delete</button>

                            <button onClick={()=>{EditData(item.id)}} className="btn btn-primary">Edit</button>

                            <button onClick={()=>{DetailsData(item.id)}} className="btn btn-success">Show Details</button>
                        </td>
                    </tr>
                     ))}
                </tbody>
            </table>
            </div>
            </div>
        </div>
    )
}
export default App;
