// useRef() --> it reutrns mutable objects
// ref 


 
import { initializeApp } from "firebase/app";
import {createUserWithEmailAndPassword, getAuth} from "firebase/auth"
import { useRef } from "react";



function App1() {
    // for useRef these are not requried
    // const [name,setName]=useState("")
    // const [email,setEmail]=useState("")
    // const [pswd,setPswd]=useState("")
    // const [cpswd,setCpswd]=useState("")
    const name=useRef
    const email=useRef()
    const pswd=useRef()
    const cpswd=useRef()

    const firebaseConfig = {
        apiKey: "AIzaSyDHUO_Do1zOMfc4hKr0WOetLN4WN2HyvIw",
        authDomain: "student-data-fb5a1.firebaseapp.com",
        projectId: "student-data-fb5a1",
        storageBucket: "student-data-fb5a1.appspot.com",
        messagingSenderId: "602614335820",
        appId: "1:602614335820:web:72c3a36f5544ce4613e7e9",
        measurementId: "G-84TBXHESHK"
      };
      
      // Initialize Firebase
      const app = initializeApp(firebaseConfig);
      const auth=getAuth()

      const store=(e)=>{
        e.preventDefault()
        if(name=="" || email=="" || pswd=="" || cpswd==""){
            alert("all fields are mandatory")
        }
        else if(pswd != cpswd){
            alert("Password doesn't match..")
        }
        let obj={
            a:email.current.value,
            b:pswd.current.value
        }
        createUserWithEmailAndPassword(auth,obj.a,obj.b)
        .then(()=>{
            alert("Saved")
        })
        .catch((err)=>{
            alert(err)
        })
      }
      
    return (
        <div>
            <form className="card p-3 m-3" style={{width:"400px"}} onSubmit={store}>
                <div className="mb-3">
                    <label  className="form-label">Full Name</label>
                    <input ref={name}  type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                    <label  className="form-label">Email address</label>
                    <input ref={email}  type="email" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />
                </div>
                <div className="mb-3">
                    <label  className="form-label">Password</label>
                    <input ref={pswd}  type="password" className="form-control" id="exampleInputPassword1" />
                </div>
                <div className="mb-3">
                    <label  className="form-label">Confirm Password</label>
                    <input ref={cpswd} type="password" className="form-control" id="exampleInputPassword1" />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}
export default App1;
