// ==================================================================================================================================================
// DAY - 8 => MAP Method

import { useEffect , useState } from "react";
import ""

function App(){
    const [data,setData]=useState(null)
    useEffect(()=>{
        fetch("https://jsonplaceholder.typicode.com/todos")      //Get data from one server to another server is called fetch  function
        .then((res)=>{
            return res.json()
        })
        .then((resp)=>{
            console.log(resp)
            setData(resp)
         })
    })
    return(
        <div>
            <h1>Fetch Data From Backend</h1>
            <table>
                <thead>
                    <tr>
                        <th>UserId</th>
                        <th>ID</th>
                        <th>Title</th>
                        <th>completed</th>
                    </tr>
                </thead>
                <tbody>
                    {data.map((item)=>(


                    <tr key={item.id}>
                        <td>{item.userId}</td>
                        <td>{item.id}</td>
                        <td>{item.title}</td>
                        <td></td>
                    </tr>
                     ))}
                </tbody>
            </table>
        </div>
    )
}
export default App;
